</p>";s:7:"adminid";s:1:"1";s:4:"type";s:12:"币友说币";s:4:"hits";s:1:"0";s:6:"footer";s:1:"1";s:5:"index";s:1:"1";s:4:"sort";s:1:"2";s:7:"addtime";s:10:"1480326159";s:7:"endtime";s:10:"1480326168";s:6:"status";s:1:"1";s:3:"img";s:0:"";}i:5;a:13:{s:2:"id";s:2:"14";s:5:"title";s:24:"有问题可联系客服";s:7:"content";s:35:"有问题请联系客服QQ:56703338";s:7:"adminid";s:1:"1";s:4:"type";s:12:"币友说币";s:4:"hits";s:1:"0";s:6:"footer";s:1:"1";s:5:"index";s:1:"1";s:4:"sort";s:1:"1";s:7:"addtime";s:10:"1480224182";s:7:"endtime";s:10:"1480224192";s:6:"status";s:1:"1";s:3:"img";s:0:"";}}}